// Track a lead conversion.
export function trackConversion() {
  if (process.env.NODE_ENV === "production") {
    const fbq = (window as any).fbq;
    if (fbq) {
      fbq("track", "Lead");
      console.log("converted");
    } else {
      console.error("fbq not loaded");
    }
  } else {
    console.log("Would convert in prod");
  }
}
